function getCCMatchStatus(oper, spcVal, curVal) {
    var temp = true;
    switch (oper) {
        case 'nu' :
            temp = (curVal == '' || curVal == null || curVal == undefined) ? true : false;
            break;
        case 'nn' :
            temp = (curVal != '' && curVal != null && curVal != undefined) ? true : false;
            break;
        case 'in' :
            if ($.isArray(curVal)) {
                temp = ($.inArray(spcVal, curVal) != "-1") ? true : false;
            } else {
                if (!$.isArray(spcVal) && typeof spcVal != undefined) {
                    spcVal = spcVal.split(",");
                } else {
                    spcVal = [];
                }
                temp = ($.inArray(curVal, spcVal) != "-1") ? true : false;
            }
            break;
        case 'ni' :
            if ($.isArray(curVal)) {
                temp = ($.inArray(spcVal, curVal) == "-1") ? true : false;
            } else {
                if (!$.isArray(spcVal) && typeof spcVal != undefined) {
                    spcVal = spcVal.split(",");
                } else {
                    spcVal = [];
                }
                temp = ($.inArray(curVal, spcVal) == "-1") ? true : false;
            }
            break;
        case 'gt' :
            if ($.isArray(curVal)) {
                temp = (curVal[0] > spcVal) ? true : false;
            } else {
                temp = (curVal > spcVal) ? true : false;
            }
            break;
        case 'ge' :
            if ($.isArray(curVal)) {
                temp = (curVal[0] >= spcVal) ? true : false;
            } else {
                temp = (curVal >= spcVal) ? true : false;
            }
            break;
        case 'lt' :
            if ($.isArray(curVal)) {
                temp = (curVal[0] < spcVal) ? true : false;
            } else {
                temp = (curVal < spcVal) ? true : false;
            }
            break;
        case 'le' :
            if ($.isArray(curVal)) {
                temp = (curVal[0] <= spcVal) ? true : false;
            } else {
                temp = (curVal <= spcVal) ? true : false;
            }
            break;
        case 'ne' :
            if ($.isArray(curVal)) {
                temp = ($.inArray(spcVal, curVal) == "-1") ? true : false;
            } else {
                temp = (curVal != spcVal) ? true : false;
            }
            break;
        default :
            if ($.isArray(curVal)) {
                temp = ($.inArray(spcVal, curVal) != "-1") ? true : false;
            } else {
                temp = (curVal == spcVal) ? true : false;
            }
            break;
    }
    return temp;
}
