function isMenuURLMatched(current, stored) {
    if (current == stored) {
        return true;
    }
    var mc_ssa = [], mc_csa = [], mc_ssp = [], mc_csp = [];
    mc_ssa = current.split("#");
    mc_csa = stored.split("#");
    if (!mc_ssa[1]) {
        return false;
    }
    if (!mc_csa[1]) {
        return false;
    }
    if (mc_csp[2] != "index") {
        return false;
    }
    mc_ssp = mc_ssa[1].split("/");
    mc_csp = mc_csa[1].split("/");
    if (mc_ssa[0] == mc_csa[0] && mc_ssp[0] == mc_csp[0] && mc_ssp[1] == mc_csp[1]) {
        return true
    }
}
